mapper.select:
 	 SELECT  bill.*  from  eforces_stayedandtroubledscan    trouble
	join  eforces_problemcause  cause on trouble.troubledtype = cause.Name 
   JOIN eforces_order bill on trouble.billsnumber=bill.Number
    where trouble.incid  = #{incid} 
    and cause.isDelete = 0 and   cause.id in 

     #{id}

    	select  * from eforces_order where isDelete = 0

			and Number in

				#{number}



    		and date_format(begintime, '%Y-%m-%d') >= #{sendStartTime,jdbcType=VARCHAR}


    		and date_format(begintime, '%Y-%m-%d') <= #{sendEndTime,jdbcType=VARCHAR} 


    		and date_format(createtime, '%Y-%m-%d') >= #{inputStartTime,jdbcType=VARCHAR}


    		and date_format(createtime, '%Y-%m-%d') <= #{inputEndTime,jdbcType=VARCHAR}


    		and remark like CONCAT('%',#{remark},'%')


    		and price = #{modeprice}


    		and fromname like concat('%' , #{fromname, jdbcType=VARCHAR},'%')


    		and toname like concat('%' , #{toname, jdbcType=VARCHAR},'%')

		SELECT  bill.* from eforces_order bill JOIN eforces_receivedscan  receive on bill.number = receive.billsnumber  
  			where  bill.isDelete = 0  and receive.incid = #{incid}
		 and receive.billsnumber  not  in (
			select DISTINCT sent.billsnumber from eforces_sentscan  sent  where sent.incid = #{incid} 
			Union 
			SELECT DISTINCT transport.billsnumber from eforces_transportedscan transport where transport.incid = #{incid}
		)

		SELECT  bill.* from eforces_order bill JOIN eforces_rectoorder  recto on bill.number = recto.Number  
  			where  bill.isDelete = 0  and recto.DepartId  = #{incid}
		and  recto.Number  not  in (
			select DISTINCT sent.billsnumber from eforces_sentscan  sent  where sent.incid = #{incid}
			Union 
			SELECT DISTINCT transport.billsnumber from eforces_transportedscan transport where transport.incid = #{incid} 
		)

	   SELECT  bill.* from eforces_order bill JOIN eforces_receivedscan receive on bill.number = receive.billsnumber   where bill.isDelete = 0  and receive.incid = #{incid}  and  bill.number in (
 			SELECT number  from eforces_customersign  
		)

select
,
from eforces_order where Id = #{id,jdbcType=INTEGER}

  		select , scan.createtime as scanTime , scan.incname  as scanIncName,
  		scan.scanners, scan.scantype, scan.nextstopname as stopname, scan.flightsnumber
  		from eforces_order  orderInfo join eforces_sentscan  scan  on orderInfo.Number =  scan.billsnumber 
  		where  1 = 1

  			and orderInfo.Number  in 

		      #{numbers}




  			and  scan.goodstype =  #{goodsType}


  			and  scan.nextstop = #{incNumber}


  			and orderInfo.Toname = #{toName}


  			and scan.expresstype = #{expresstype}


  			and scan.incid = #{scanIncNumber}


  			and scan.scannerid = #{scannerNumber}


  			and scan.flightsnumber = #{flightsnumber}


  			and scan.createtime >= #{scanStartTime}


  			and scan.createtime <= #{scanEndTime}

  		select , scan.createtime as scanTime , scan.incname  as scanIncName,
  		scan.scanners, scan.scantype, scan.laststopname as  stopname , scan.flightsnumber
  		from eforces_order  orderInfo join eforces_receivedscan  scan  on orderInfo.Number =  scan.billsnumber 
  		where  1 = 1

  			and orderInfo.Number  in 

		      #{numbers}



  			and  scan.goodstype =  #{goodsType}



  			and orderInfo.Toname = #{toName}


  			and scan.expresstype = #{expresstype}


  			and scan.incid = #{scanIncNumber}


  			and scan.scannerid = #{scannerNumber}


  			and scan.flightsnumber = #{flightsnumber}


  			and scan.createtime >= #{scanStartTime}


  			and scan.createtime <= #{scanEndTime}

  		select ,  scan.createtime as scanTime , scan.incname  as scanIncName,
  		scan.scanners, scan.scantype, '' as  stopname , scan.flightsnumber
  		from eforces_order  orderInfo join eforces_distributedscan  scan  on orderInfo.Number =  scan.billsnumber 
  		where  1 = 1

  			and orderInfo.Number  in 

		      #{numbers}



  			and  scan.goodstype =  #{goodsType}


  			and orderInfo.Toname = #{toName}


  			and scan.expresstype = #{expresstype}


  			and scan.incid = #{scanIncNumber}


  			and scan.scannerid = #{scannerNumber}


  			and scan.flightsnumber = #{flightsnumber}


  			and scan.createtime >= #{scanStartTime}


  			and scan.createtime <= #{scanEndTime}

  		select ,  scan.SignTime as scanTime , scan.incname  as scanIncName,
  		scan.deliveryUserName  as scanners , '客户签收' as scantype , '' as  stopname , ''  as flightsnumber
  		from eforces_order  orderInfo join eforces_customersign  scan  on orderInfo.Number =  scan.Number 
  		where  1 = 1

  			and orderInfo.Number  in 

		      #{numbers}



  			and orderInfo.Toname = #{toName}


  			and scan.incid = #{scanIncNumber}


  			and scan.DeliveryUserId = #{scannerNumber}


  			and scan.createtime >= #{scanStartTime}


  			and scan.createtime <= #{scanEndTime}

  		select , scan.createtime as scanTime , scan.incname  as scanIncName,
  		scan.scanners, scan.scantype, '' as stopname, scan.flightsnumber
  		from eforces_order  orderInfo join eforces_sentscan  scan  on orderInfo.Number =  scan.billsnumber 
